package com.nissan.app;

public class q1 {
/*C/Ob2/ h. Implement a logic to swap two numbers without using a temporary variable.
You can set the status of your assignment here.*/
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5,b=10;
		System.out.println(" nos before swapping:"+a+" " +b);
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println(" nos after swapping:"+ a+" " +b);

	}

}
